--	FETCH NEXT FROM walker INTO @catalog;
--END;

--CLOSE walker;
--DEALLOCATE walker; ';

--		DECLARE @currentDBName sysname;
--		DECLARE @targets table (
--			row_id int IDENTITY(1, 1) NOT NULL,
--			[db_name] sysname NOT NULL
--		);

--		INSERT INTO @targets 
