--			@Targets = @CycleFTCrawlLogsInDatabases, 
--			@ExcludeClones = 1, 
--			@ExcludeSecondaries = 1, 
--			@ExcludeSimpleRecovery = 0, 
--			@ExcludeReadOnly = 1, 
