USE [admindb];
GO

IF OBJECT_ID('dbo.[filtered_index_obstacles]','P') IS NOT NULL
	DROP PROC dbo.[filtered_index_obstacles];
GO

CREATE PROC dbo.[filtered_index_obstacles]
	@databases					nvarchar(MAX)			= N'{USER}', 
	@priorities					nvarchar(MAX)			= NULL
AS
    SET NOCOUNT ON; 

	-- [v12.8.5293.3] - License, Code, & Docs: https://github.com/overachiever-productions/s4/ 

	SET @databases  = ISNULL(NULLIF(@databases, N''), N'{USER}');
	SET @priorities = NULLIF(@priorities, N'');

	/*---------------------------------------------------------------------------------------------------------------------------------------------------
	-- Server Level Defaults. 
	--		These are a PAIN. 
	--		sys.configurations 'user options' is ... STUPID. 
	--			- It SHOULD default to a MASK of all of the enabled/disabled options. 
	--			- Instead, it defaults to 0. 
	--			ONLY if someone has modified one of the settings, explicitly, does it become anything OTHER than 0. 
	--			BUT, that's the rub. If I want to FORCE ARITHABORT to ON, I'd do something like: `EXEC sp_configure 'user options', 64); RECONFIGURE;` etc... 
	--			Except: 
	--				The docs do NOT cover whether the above operation just a) flipped ARITHABORT to ON or b) flipped ARITHABORT from the server-default (or c) disables the option (really doubt this)). 
	--					LOGICALLY?? option a) is the obvious? choice. 
	--					Except... guess what? 
	--						IF 'user options' is now 64 - wouldn't that, in turn, mean that 8, 15, 32, 128, 256, etc. (since they're NOT included in the bit-mask) are now OFF? 
	--			Seriously:
	--				Seems to me that you either spam in a bitmask of EVERYTHING you want enabled/disabled or ... changing a SINGLE option to (presumably) 'ON' ...disables all of the others. 
	--				Right? 
	---------------------------------------------------------------------------------------------------------------------------------------------------*/
	CREATE TABLE #userOptions (
		[row_id] int IDENTITY(1,1) NOT NULL,
		[mask] bigint NOT NULL, 
		[option] sysname NOT NULL, 
		[warning] sysname NOT NULL 
	);

	DECLARE @BitMask int = (SELECT CAST([value_in_use] AS bigint) FROM sys.[configurations] WHERE [name] = N'user options');

	IF @BitMask <> 0 BEGIN

		WITH options AS (
			SELECT [option], [mask] FROM (VALUES 
				(8		, N'ANSI_WARNINGS'),
				(16		, N'ANSI_PADDING'),
				(32		, N'ANSI_NULLS'),
				(64		, N'ARITHABORT'),
				(256	, N'QUOTED_IDENTIFIER'),
				--(1024	, N'ANSI_NULL_DFLT_ON'),			-- handled down below... 
				(4096	, N'CONCAT_NULL_YIELDS_NULL') --,	
			) [opts]([mask], [option])
		), 
		matched AS ( 
			SELECT 
				[mask], 
				[option], 
				CASE WHEN @BitMask & [mask] > 0 THEN [option] ELSE N'' END [matched]
			FROM 
				[options]
		) 

		INSERT INTO [#userOptions] ([mask], [option], [warning])
		SELECT 
			[mask],
			[option],
			N'Default Connection Settings (via sp_configure ''user options'') does NOT have ' + [matched].[option] + N' set to ON.' [warning]
		FROM 
			[matched]
		WHERE 
			[matched].[matched] = N''

		IF @BitMask & 1024 > 0 BEGIN 
			DELETE FROM [#userOptions] WHERE [mask] = 32; -- ANSI_NULL_DFLT_ON ... obviously overrides ANSI_NULLs... 
		END;

		IF @BitMask & 8192 > 0 BEGIN  -- ENABLED - which it should NOT BE. 
			INSERT INTO [#userOptions] ([mask], [option], [warning]) VALUES(8192, N'NUMERIC_ROUNDABORT', N'Default Connection Settings (via sp_configure ''user options'') has NUMERIC_ARITHABORT set to ON.');
		END;

		IF @BitMask & 2048 > 0 BEGIN 
			INSERT INTO [#userOptions] ([mask], [option], [warning]) VALUES(8192, N'ANSI_NULL_DFLT_OFF', N'Default Connection Settings (via sp_configure ''user options'') has ANSI_NULL_DFLT_OFF set to ON.');
		END;
	END;
	
	/*---------------------------------------------------------------------------------------------------------------------------------------------------
	-- Default Database Settings that do NOT match settings REQUIRED for Filtered Indexes: 
			NOTE: 
				a) these are RARELY set 'correctly' or to anything other than 0 "across the board" for all of the settings in question. 
				b) and, insanely enough, this typically never 'matters' - because DEFAULTs via @@OPTIONS/sys.configurations 'user options' ... are typically set correctly. 

	---------------------------------------------------------------------------------------------------------------------------------------------------*/
	CREATE TABLE #dbSettings (
		[row_id] int IDENTITY(1,1) NOT NULL,
		[name] [sysname] NOT NULL,
		[database_id] [int] NOT NULL,
		[compatibility_level] [tinyint] NOT NULL,
		[is_ansi_null_default_on] [bit] NULL,
		[is_ansi_nulls_on] [bit] NULL,
		[is_ansi_padding_on] [bit] NULL,
		[is_ansi_warnings_on] [bit] NULL,
		[is_arithabort_on] [bit] NULL,
		[is_concat_null_yields_null_on] [bit] NULL,
		[is_numeric_roundabort_on] [bit] NULL,
		[is_quoted_identifier_on] [bit] NULL
	); 

	INSERT INTO [#dbSettings] (
		[name],
		[database_id],
		[compatibility_level],
		[is_ansi_null_default_on],
		[is_ansi_nulls_on],
		[is_ansi_padding_on],
		[is_ansi_warnings_on],
		[is_arithabort_on],
		[is_concat_null_yields_null_on],
		[is_numeric_roundabort_on],
		[is_quoted_identifier_on]
	)
	SELECT 
		[name],
		[database_id],
		[compatibility_level],
		[is_ansi_null_default_on],
		[is_ansi_nulls_on],
		[is_ansi_padding_on],
		[is_ansi_warnings_on],
		[is_arithabort_on],
		[is_concat_null_yields_null_on],
		[is_numeric_roundabort_on],
		[is_quoted_identifier_on]
	FROM 
		sys.databases
	WHERE 
		CASE WHEN [is_ansi_nulls_on] = 0 AND [is_ansi_null_default_on] = 0 THEN 0 ELSE 1 END = 0
		OR CASE WHEN ([is_arithabort_on] = 1 OR ([compatibility_level] >= 90 AND [is_ansi_warnings_on] = 1)) THEN 1 ELSE 0 END = 0
		OR [is_ansi_nulls_on] = 0
		OR [is_ansi_padding_on] = 0
		OR [is_ansi_warnings_on] = 0
		OR [is_arithabort_on] = 0
		OR [is_concat_null_yields_null_on] = 0
		OR [is_numeric_roundabort_on] = 1
		OR [is_quoted_identifier_on] = 0;

	/*---------------------------------------------------------------------------------------------------------------------------------------------------
	-- Active Connections
	---------------------------------------------------------------------------------------------------------------------------------------------------*/
	DECLARE @minCompat tinyint = (SELECT MIN([compatibility_level]) FROM sys.databases);

	WITH core AS ( 

		SELECT 
			[s].[session_id],
			[s].[quoted_identifier], 
			[s].[arithabort], 
			[s].[ansi_null_dflt_on],  -- what mess. there's also ansi_null_dflt_off - and DRIVERS set different values for these settings (as is possible): https://learn.microsoft.com/en-us/sql/t-sql/statements/set-ansi-null-dflt-on-transact-sql?view=sql-server-ver17
			[s].[ansi_defaults], -- see notes below... 
			[s].[ansi_warnings], 
			[s].[ansi_padding],  
			[s].[ansi_nulls],  
			[s].[concat_null_yields_null], -- deprecated, should ALWAYS be set to ON. 
			CASE 
				WHEN [s].[arithabort] = 1 THEN N'ON (EXPLICIT)'
				WHEN ([s].[arithabort] = 0 AND [s].[ansi_warnings] = 1) THEN 
					CASE 
						WHEN @minCompat < 90 THEN N'ON (IMPLICIT) - CROSS DB WARNING' -- https://learn.microsoft.com/en-us/sql/t-sql/statements/set-arithabort-transact-sql?view=sql-server-ver16#remarks
						ELSE N'ON (IMPLICIT)'
					END
				WHEN [s].[arithabort] = 0 AND [s].[ansi_warnings] = 0 THEN N'OFF (ANSI_WARNINGS = 0)'
				ELSE '##ERROR##'
			END [arithabort_status],
			[s].[login_time],
			[s].[host_name],
			[s].[program_name],
			[s].[login_name],
			[s].[nt_domain],
			[s].[nt_user_name],
			[s].[status],
			[s].[context_info],
			[s].[cpu_time],
			[s].[memory_usage],
			[s].[total_elapsed_time],
			[s].[endpoint_id],
			[s].[last_request_end_time],
			[s].[reads],
			[s].[logical_reads],
			[s].[is_user_process],
			[s].[transaction_isolation_level],
			[s].[original_login_name],
			[d].[name] [current_database], 
			[ad].[name] [authenticating_database],
			[d].[compatibility_level] [current_database_compat_level]
		FROM 
			sys.[dm_exec_sessions] [s]
			LEFT OUTER JOIN sys.[databases] [d] ON [s].[database_id] = [d].[database_id]
			LEFT OUTER JOIN sys.[databases] [ad] ON [s].[authenticating_database_id] = [ad].[database_id]
		WHERE 
			([s].[quoted_identifier] = 0 AND [s].[is_user_process] = 1)
			OR ([s].[ansi_nulls] = 0 AND [s].[is_user_process] = 1)
			OR ([s].[arithabort] = 0 AND [s].[is_user_process] = 1)
			OR ([s].[ansi_padding] = 0 AND [s].[is_user_process] = 1)
			-- i've personally flipped this a FEW times in some of my code.  ah. but there's a crazy interplay between ANSI_WARNINGS and ... ARITHABORT. 
			--  see this on ANSI_WARNINGS: https://www.notion.so/overachiever/Connection-Options-98e84166fd504cf9b39fdd86e6c0a5fa?source=copy_link#df55c0a75c63434494ae3b5369ebd6a5
			OR ([s].[ansi_warnings] = 0 AND [s].[is_user_process] = 1) 
	
			-- ansi_defaults is the same as "turn on a whole family of ANSI_xxx" settings - to their (good/ANSI) defaults: 
			-- https://learn.microsoft.com/en-us/sql/t-sql/statements/set-ansi-defaults-transact-sql?view=sql-server-ver17
			-- I'm pretty sure I can drop/ignore this one - it only adds complexity and NOISE, with no real benefit. 
			--		ah, actually, i THINK I should do this: a) don't evaluate it for smells/etc. b) do 'publish' it as part of the output/projection.
			-- OR ([ansi_defaults] = 0 AND [is_user_process] = 1)  -- arguably... not a problem IF all of the other settings that we care about are 'defaulted' correctly.
			OR ([s].[concat_null_yields_null] = 0 AND [s].[is_user_process] = 1)
	) 

	SELECT 
		IDENTITY(int, 1, 1) [row_id],
		[session_id],
		[arithabort_status],
		CASE 
			WHEN [is_user_process] = 1 AND [quoted_identifier] = 0 THEN N'QUOTED_IDENTIFIERS = OFF; ' -- might want to point out that THIS one is a BIGGIE.
			WHEN [is_user_process] = 1 AND [ansi_nulls] = 0 THEN N'ANSI_NULLS = OFF; '
			WHEN ([is_user_process] = 1 AND [arithabort_status] LIKE N'OFF%') THEN N'ARITHABORT = OFF; '
			WHEN [is_user_process] = 1 AND [ansi_padding] = 0 THEN N'ANSI_PADDING = OFF; '
			WHEN [is_user_process] = 1 AND [ansi_warnings] = 0 THEN N'ANSI_WARNINGS = OFF; '
			WHEN [is_user_process] = 1 AND [concat_null_yields_null] = 0 THEN N'CONCAT_NULL_YIELDS_NULL = OFF; '
			ELSE N''
		END [warning],

		CASE 
			WHEN ([is_user_process] = 1 AND [quoted_identifier] = 0) AND [program_name] LIKE N'SQLAgent%' THEN N'The SQL Server Agent does NOT connect with QUOTED_IDENTIFIER or ANSI_XXX set to ON.'

			-- HMM. according to the DOCS, SSMS defaults Arithabort to ON... but I do NOT see that in the wild (SSMS 21 - for example, defaults to OFF)
			--		https://learn.microsoft.com/en-us/sql/t-sql/statements/set-arithabort-transact-sql?view=sql-server-ver16
			-- ah. i THINK they claim this because SSMS sets ANSI_WARNINGs = ON ... which is fine/great - except/unless you've got a wretched DB somewhere using compat 80 or lower. i mean - mega-bletch. but, still. 
			WHEN ([is_user_process] = 1 AND [arithabort] = 0) AND [program_name] LIKE N'%Management Studio%' THEN 'SSMS defaults to arithabort = 0 (off), which is odd....'
			ELSE N''
		END [mitigation], 
		CASE 
			WHEN ([is_user_process] = 1 AND [quoted_identifier] = 0) THEN N'https://www.totalsql.tips/xx/some-key'
			ELSE N''
		END [recommendation],
		N' ' [ ],

		-- HMM... i might want to just dump all of these details into XML and call it [context]?
		[quoted_identifier],
		[arithabort],
		[ansi_null_dflt_on],
		[ansi_defaults],
		[ansi_warnings],
		[ansi_padding],
		[ansi_nulls],
		[concat_null_yields_null],
		N'' [], -- nbsp
		[login_time],
		[host_name],
		[program_name],
		[login_name],
		[nt_domain],
		[nt_user_name],
		[status],
		[context_info],
		[cpu_time],
		[memory_usage],
		[total_elapsed_time],
		[endpoint_id],
		[last_request_end_time],
		[reads],
		[logical_reads],
		[is_user_process],
		[transaction_isolation_level],
		[original_login_name],
		[current_database],
		[authenticating_database] 
		[current_database_compat_level],
		@minCompat [minimum_database_compat_level]
	INTO 
		#currentUserConnections
	FROM 
		core
	ORDER BY 
		[core].[session_id];

	/* Ignore/Remove rows that report `ON (IMPLICIT)` for arithabort_status ... and which have NO OTHER issues/warnings/problems.  */
	DELETE FROM [#currentUserConnections] WHERE [warning] = N'';

	/*---------------------------------------------------------------------------------------------------------------------------------------------------
	-- Exclude Results from Databases not targeted by @databases
	---------------------------------------------------------------------------------------------------------------------------------------------------*/
	DECLARE @sqlAgentConnectionsFound bit = 0;
	IF EXISTS (SELECT NULL FROM [#currentUserConnections] WHERE [program_name] LIKE N'SQLAgent -%')
		SET @sqlAgentConnectionsFound = 1;

	IF UPPER(@databases) <> N'{USER}' BEGIN
		
		DECLARE @targetDatabases table (
			[row_id] int IDENTITY(1,1) NOT NULL, 
			[database_name] sysname NOT NULL 
		);

		DECLARE @xmlOutput xml;
		EXEC dbo.[targeted_databases]
			@Databases = @databases,
			@Priorities = @priorities,
			@ExcludeClones = 1,
			@ExcludeSecondaries = 1,
			@ExcludeSimpleRecovery = 0,
			@ExcludeReadOnly = 1,
			@ExcludeRestoring = 1,
			@ExcludeRecovering = 1,
			@ExcludeOffline = 1,
			@SerializedOutput = @xmlOutput OUTPUT;

		WITH shredded AS ( 
			SELECT 
				[data].[row].value('@id[1]', 'int') [row_id], 
				[data].[row].value('.[1]', 'sysname') [database_name]
			FROM 
				@xmlOutput.nodes('//database') [data]([row])
		)

		INSERT INTO @targetDatabases ([database_name])
		SELECT [database_name] FROM [shredded] ORDER BY [row_id];

		DELETE FROM [#dbSettings] WHERE [name] NOT IN (SELECT [database_name] FROM @targetDatabases);
		DELETE FROM [#currentUserConnections] WHERE [current_database] NOT IN (SELECT [database_name] FROM @targetDatabases);
	END;

	/*---------------------------------------------------------------------------------------------------------------------------------------------------
	-- Tables & Columns:
	---------------------------------------------------------------------------------------------------------------------------------------------------*/
	CREATE TABLE #tables (
		[row_id] int IDENTITY(1,1) NOT NULL,
		[database] sysname NOT NULL,
		[table] nvarchar(257) NULL,
		[type_desc] nvarchar(60) NULL,
		[uses_ansi_nulls] bit NULL
	);

	CREATE TABLE #columns (
		[row_id] int IDENTITY(1,1) NOT NULL,
		[database] sysname NOT NULL,
		[parent] nvarchar(257) NULL,
		[column] sysname NULL,
		[column_id] int NOT NULL,
		[type] sysname NULL,
		[is_ansi_padded] bit NOT NULL
	);

	DECLARE @sql nvarchar(MAX) = N'USE [{CURRENT_DB}];
	INSERT INTO [#tables] ([database], [table], [type_desc], [uses_ansi_nulls])
	SELECT
		N''[{CURRENT_DB}]'' [database],
		SCHEMA_NAME([schema_id]) + N''.'' + [name] [table],
		[type_desc],
		[uses_ansi_nulls]
	FROM 
		sys.[tables]
	WHERE 
		[uses_ansi_nulls] = 0; ';
	
	DECLARE @errors xml;
	EXEC dbo.[execute_per_database]
		@Databases = @databases,
		@Priorities = @priorities,
		@Statement = @sql,
		@Errors = @errors OUTPUT;

	IF @errors IS NOT NULL BEGIN 
		RAISERROR(N'Unexpected error. See [Errors] XML for more details.', 16, 1);
		SELECT @errors;
		RETURN -100;
	END;
	
	SET @sql = N'USE [{CURRENT_DB}];
	INSERT INTO [#columns] ([database], [parent], [column], [column_id], [type], [is_ansi_padded])
	SELECT 
		N''[{CURRENT_DB}]'' [database],
		SCHEMA_NAME([o].[schema_id]) + N''.'' + OBJECT_NAME([c].[object_id]) [parent], 
		[c].[name] [column], 
		[c].[column_id], 
		TYPE_NAME([c].[system_type_id]) [type], 
		[c].[is_ansi_padded]
	FROM 
		sys.columns [c]
		LEFT OUTER JOIN sys.[objects] [o] ON [c].[object_id] = [o].[object_id]
	WHERE 
		TYPE_NAME([c].[system_type_id]) LIKE N''%char%'' AND TYPE_NAME([c].[system_type_id]) NOT LIKE N''%MAX%)''
		AND [c].[is_ansi_padded] = 0
	ORDER BY 
		1; ';

	SET @errors = NULL; 
	EXEC dbo.[execute_per_database]
		@Databases = @databases,
		@Priorities = @priorities,
		@Statement = @sql,
		@Errors = @errors OUTPUT;
	
	IF @errors IS NOT NULL BEGIN 
		RAISERROR(N'Unexpected error. See [Errors] XML for more details.', 16, 1);
		SELECT @errors;
		RETURN -100;
	END;

	/*---------------------------------------------------------------------------------------------------------------------------------------------------
	-- Modules:
	---------------------------------------------------------------------------------------------------------------------------------------------------*/
	CREATE TABLE #modules (
		[row_id] int IDENTITY(1,1) NOT NULL,
		[database] sysname NOT NULL,
		[module_name] nvarchar(257) NULL,
		[definition] nvarchar(max) NULL,
		[quoted_identifier] bit NULL,
		[ansi_nulls] bit NULL
	);
	
	SET @sql = N'USE [{CURRENT_DB}];
	INSERT INTO [#modules] ([database], [module_name], [definition], [quoted_identifier], [ansi_nulls])
	SELECT 
		N''[{CURRENT_DB}]'' [database],
		SCHEMA_NAME([o].[schema_id]) + N''.'' + OBJECT_NAME([m].[object_id]) [name], 
		[m].[definition], 
		[m].[uses_quoted_identifier] [quoted_identifier], 
		[m].[uses_ansi_nulls] [ansi_nulls]
	FROM 
		sys.[sql_modules] [m]
		LEFT OUTER JOIN sys.[objects] [o] ON [m].[object_id] = [o].[object_id]
	WHERE 
		[m].[uses_ansi_nulls] = 0 
		OR [m].[uses_quoted_identifier] = 0; ';

	SET @errors = NULL; 
	EXEC dbo.[execute_per_database]
		@Databases = @databases,
		@Priorities = @priorities,
		@Statement = @sql,
		@Errors = @errors OUTPUT;
	
	IF @errors IS NOT NULL BEGIN 
		RAISERROR(N'Unexpected error. See [Errors] XML for more details.', 16, 1);
		SELECT @errors;
		RETURN -100;
	END;

	/*---------------------------------------------------------------------------------------------------------------------------------------------------
	-- Reporting
	---------------------------------------------------------------------------------------------------------------------------------------------------*/
	CREATE TABLE #findings (
		[row_id] int IDENTITY(1,1) NOT NULL,
		[key] sysname NOT NULL,
		[obstacle] sysname NOT NULL, 
		[reference] sysname NOT NULL 
	);

	IF EXISTS (SELECT NULL FROM [#userOptions]) BEGIN
		INSERT INTO [#findings] ([key], [obstacle], [reference])
		VALUES (
			N'DEFAULT_USER_OPTIONS',
			N'Default Settings for sp_configure ''user options'' have been modified. Default Value: [0]. Current value: [' + CAST(@BitMask AS sysname) +  N']',
			N'https://www.totalsql.com/xxx/short-code-here' 
		);
	END;

	IF EXISTS (SELECT NULL FROM [#dbSettings]) BEGIN
		INSERT INTO [#findings] ([key], [obstacle], [reference])
		VALUES (
			N'DB_SETTINGS',
			N'Default options for one or more databases do NOT match required settings for Filtered Indexes.', 
			N'https://www.totalsql.com/xxx/short-code-here2'
		);
	END;

	IF @sqlAgentConnectionsFound = 1 BEGIN
		INSERT INTO [#findings] ([key], [obstacle], [reference])
		VALUES (
			N'SQL_AGENT_CONNECTIONS', 
			N'The SQL Server Agent is Connected. It connects with QUOTED_IDENTIFIERS = OFF.', 
			N'https://www.totalsql.com/xxx/short-code-here7' -- need to make sure to mention, too, that ansi_warnings ON + COMPAT > 90 is required for equivalent of ARITHABORT = ON. 
		);		
	END;

	IF EXISTS (SELECT NULL FROM [#currentUserConnections]) BEGIN
		INSERT INTO [#findings] ([key], [obstacle], [reference])
		VALUES (
			N'CURRENT_USER_CONNECTIONS', 
			N'One or more user connections has SET OPTIONS that do NOT match required settings for Filtered Indexes.', 
			N'https://www.totalsql.com/xxx/short-code-here3'
		);
	END;

	IF EXISTS (SELECT NULL FROM [#tables]) BEGIN
		INSERT INTO [#findings] ([key], [obstacle], [reference])
		VALUES (
			N'TABLE_SETTINGS',
			N'One or more table definitions uses options that are incompatible with required settings for Filtered Indexes.', 
			N'https://www.totalsql.com/xxx/short-code-here4'
		);
	END;

	IF EXISTS (SELECT NULL FROM [#columns]) BEGIN
		INSERT INTO [#findings] ([key], [obstacle], [reference])
		VALUES (
			N'COLUMN_SETTINGS',
			N'One or more columns uses options that are incompatible with required settings for Filtered Indexes.', 
			N'https://www.totalsql.com/xxx/short-code-here5'
		);
	END;

	IF EXISTS (SELECT NULL FROM [#modules]) BEGIN
-- TODO: DELETE from modules that aren't triggers or sprocs. 
--			i.e., delete funcs (not even sure they can have their own, 'BAD', settings ... but no sense reporting on them. 
		INSERT INTO [#findings] ([key], [obstacle], [reference])
		VALUES (
			N'MODULE_SETTINGS',
			N'One or more Triggers or Stored Procedures uses options that are incompatible with required settings for Filtered Indexes..', 
			N'https://www.totalsql.com/xxx/short-code-here6'
		);
	END;

	SELECT
		[key],
		[obstacle],
		[reference]
	FROM
		[#findings]
	ORDER BY 
		[row_id];

	IF EXISTS (SELECT NULL FROM [#findings]) 
		SELECT N'REQUIRED_SETTINGS' [key], [x].[setting], [x].[required_value], [x].[notes] FROM (VALUES 
			(N'ANSI_NULLS', N'ON', N'ANSI_NULLS_DFLT_ON = 1 is Equivalent of ANSI_NULLS = 1'),
			(N'ANSI_PADDING', N'ON', N''),
			(N'ANSI_WARNINGS', N'ON', N''),
			(N'ARITHABORT', N'ON', N'In DBs with compat >= 90, ANSI_WARNINGS = 1 is equivalent to ARITHABORT = 1'),
			(N'CONCAT_NULL_YIELDS_NULL', N'ON', N''),
			(N'QUOTED_IDENTIFIER', N'ON', N''),
			(N'NUMERIC_ROUNDABORT', N'OFF', N'')
		) [x]([setting], [required_value], [notes]);


	IF EXISTS (SELECT NULL FROM [#userOptions])
		SELECT
			N'DEFAULT_USER_OPTIONS' [key],
			[mask],
			[option],
			[warning] 
		FROM
			[#userOptions]
		ORDER BY 
			[row_id];

	IF EXISTS (SELECT NULL FROM [#dbSettings])
-- TODO: order by @priorities... 
		SELECT
			N'DATABASE_SETTINGS' [key],
			[name],
			[database_id],
			[compatibility_level],
			[is_ansi_null_default_on],
			[is_ansi_nulls_on],
			[is_ansi_padding_on],
			[is_ansi_warnings_on],
			[is_arithabort_on],
			[is_concat_null_yields_null_on],
			[is_numeric_roundabort_on],
			[is_quoted_identifier_on]
		FROM
			[#dbSettings]
		ORDER BY
			[row_id];

	IF EXISTS (SELECT NULL FROM [#currentUserConnections]) 
		SELECT 
			N'CURRENT_USER_CONNECTIONS' [key],
			[session_id],
			[arithabort_status],
			[warning],
			[mitigation],
			[recommendation],
			[ ],
			[quoted_identifier],
			[arithabort],
			[ansi_null_dflt_on],
			[ansi_defaults],
			[ansi_warnings],
			[ansi_padding],
			[ansi_nulls],
			[concat_null_yields_null],
			[],
			[login_time],
			[host_name],
			[program_name],
			[login_name],
			[nt_domain],
			[nt_user_name],
			[status],
			[context_info],
			[cpu_time],
			[memory_usage],
			[total_elapsed_time],
			[endpoint_id],
			[last_request_end_time],
			[reads],
			[logical_reads],
			[is_user_process],
			[transaction_isolation_level],
			[original_login_name],
			[current_database],
			[current_database_compat_level],
			[minimum_database_compat_level]
		FROM 
			[#currentUserConnections] 
		ORDER BY 
			[row_id];

	IF EXISTS (SELECT NULL FROM [#tables])
		SELECT
			N'TABLE_SETTINGS' [key],
			[database],
			[table],
			[type_desc],
			[uses_ansi_nulls]
		FROM
			[#tables]
		ORDER BY
			[row_id];

	IF EXISTS (SELECT NULL FROM [#columns])
		SELECT
			N'COLUMN_SETTINGS' [key],
			[database],
			[parent],
			[column],
			[column_id],
			[type],
			[is_ansi_padded]
		FROM
			[#columns]
		ORDER BY
			[row_id];

	IF EXISTS (SELECT NULL FROM [#modules])
		SELECT
			N'MODULE_SETTINGS' [key],
			[database],
			[module_name],
			[definition],
			[quoted_identifier],
			[ansi_nulls]
		FROM
			[#modules]
		ORDER BY
			[row_id];

	RETURN 0;
GO


-----------------------------------
USE [admindb];
GO

IF OBJECT_ID('dbo.script_indexes','P') IS NOT NULL
	DROP PROC dbo.[script_indexes];
GO

CREATE PROC dbo.[script_indexes]
	@TargetDatabase				sysname				= NULL, 
	@TargetTables				nvarchar(MAX)		= N'{ALL}', 
	@ExcludedTables				nvarchar(MAX)		= NULL,
	@TargetIndexes				sysname				= N'{ALL}', 
	@ExcludedIndexes			nvarchar(MAX)		= NULL,
	@ExcludeHeaps				bit					= 1,
	@IncludeSystemTables		bit					= 0, 
	@IncludeViews				bit					= 0, 
	@SerializedOutput			xml					= N'<default/>'	    OUTPUT
AS
    SET NOCOUNT ON; 

	-- [v12.8.5293.3] - License, Code, & Docs: https://github.com/overachiever-productions/s4/ 

	SET @TargetTables = ISNULL(NULLIF(@TargetTables, N''), N'{ALL}'); 
	SET @TargetIndexes = ISNULL(NULLIF(@TargetIndexes, N''), N'{ALL}'); 

	SET @ExcludedTables = NULLIF(@ExcludedTables, N'');
	SET @ExcludedIndexes = NULLIF(@ExcludedIndexes, N'');

	SET @ExcludeHeaps = ISNULL(@ExcludeHeaps, 1);

	IF @TargetDatabase IS NULL BEGIN 
		EXEC dbo.[get_executing_dbname] @ExecutingDBName = @TargetDatabase OUTPUT;
		
		IF @TargetDatabase IS NULL BEGIN 
			RAISERROR('Invalid Database-Name specified for @TargetDatabase and/or S4 was unable to determine calling-db-context. Please specify a valid database name for @TargetDatabase and retry. ', 16, 1);
			RETURN -5;
		END;
	END;

	DECLARE @crlf nchar(2) = NCHAR(13) + NCHAR(10);
    DECLARE @newAtrributeLine sysname = @crlf + NCHAR(9) + N' ';
	DECLARE @sql nvarchar(MAX);
	DECLARE @types sysname = N'''U'''; 
	IF @IncludeSystemTables = 1 SET @types = @types + N', ''S''';
	IF @IncludeViews = 1 SET @types = @types + N', ''V''';

	IF @ExcludedTables IS NOT NULL BEGIN 
		CREATE TABLE #excluded_tables (
			[table_name] sysname NOT NULL
		);

		INSERT INTO [#excluded_tables] ([table_name])
		SELECT [result] FROM dbo.split_string(@ExcludedTables, N',', 1);
	END;

	CREATE TABLE #target_tables (
		[table_name] sysname NOT NULL, 
		[object_id] int NULL
	);

	IF @TargetTables <> N'{ALL}' BEGIN 
		INSERT INTO [#target_tables] ([table_name])
		SELECT [result] FROM dbo.split_string(@TargetTables, N',', 1);

		IF EXISTS (SELECT NULL FROM [#target_tables] WHERE [table_name] LIKE N'%`%%' ESCAPE N'`') BEGIN 
			SET @sql = N'SELECT 
				[t].[name]
			FROM 
				[{0}].sys.[objects] [t]
				LEFT OUTER JOIN [#target_tables] [x] ON [t].[name] LIKE [x].[table_name]
			WHERE 
				[t].[type] IN ({types})
				AND [x].[table_name] LIKE N''%`%'' ESCAPE N''`''; '; 

			SET @sql = REPLACE(@sql, N'{types}', @types);
			SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);

			INSERT INTO [#target_tables] ([table_name]) 
			EXEC sys.sp_executesql 
				@sql; 

			DELETE FROM [#target_tables] WHERE [table_name] LIKE N'%`%%' ESCAPE N'`';
		END;

		SET @sql = N'USE [' + @TargetDatabase + N'];
		UPDATE #target_tables 
		SET 
			[object_id] = OBJECT_ID([table_name])
		WHERE 
			[object_id] IS NULL; ';

		EXEC sp_executesql 
			@sql;

		-- MKC: need to 'pre-exclude' here to avoid error about NOT finding an explicitly defined table. (AND need to exclude below as well).
		IF @ExcludedTables IS NOT NULL BEGIN 
			DELETE [t] 
			FROM 
				[#target_tables] [t] 
				INNER JOIN [#excluded_tables] [x] ON [t].[table_name] LIKE [x].[table_name];
		END;
		
		IF EXISTS (SELECT NULL FROM [#target_tables] WHERE [object_id] IS NULL) BEGIN 
			SELECT [table_name] [target_table], [object_id] FROM [#target_tables] WHERE [object_id] IS NULL; 

			RAISERROR(N'One or more supplied @TargetTables could not be identified (i.e., does not have a valid object_id). Please remove or correct.', 16, 1);
			RETURN -11;
		END;
	END;

	CREATE TABLE #target_indexes (
		[object_name] sysname NOT NULL, 
		[object_id] sysname NOT NULL, 
		[schema_id] int NOT NULL,
		[object_type] char(1) NOT NULL,
		[index_id] int NOT NULL, 
		[index_name] sysname NULL 
		-- other cols here as needed
	);

	SET @sql = N'	SELECT 
		[o].[name], 
		[o].[object_id], 
		[o].[schema_id], 
		[o].[type],
		[i].[index_id], 
		[i].[name]
	FROM 
		[{0}].sys.objects [o] 
		INNER JOIN [{0}].sys.indexes [i] ON [o].[object_id] = [i].[object_id] 
	WHERE 
		[o].[type] IN ({types}){filter}; ';

	SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);
	SET @sql = REPLACE(@sql, N'{types}', @types);
	
	IF UPPER(@TargetTables) = N'{ALL}' BEGIN 
		SET @sql = REPLACE(@sql, N'{filter}', N'');
	  END; 
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{filter}', @newAtrributeLine + NCHAR(9) + N'AND [o].[name] IN (SELECT [table_name] FROM [#target_tables])');
	END;

	INSERT INTO [#target_indexes] ([object_name], [object_id], [schema_id], [object_type], [index_id], [index_name])
	EXEC sys.sp_executesql 
		@sql;

	IF @ExcludedTables IS NOT NULL BEGIN 
		DELETE [t] 
		FROM 
			[#target_indexes] [t] 
			INNER JOIN [#excluded_tables] [x] ON [t].[object_name] LIKE [x].[table_name];
	END; 

	IF @ExcludedIndexes IS NOT NULL BEGIN 
		DELETE [t] 
		FROM 
			[#target_indexes] [t] 
			INNER JOIN (SELECT [result] FROM dbo.[split_string](@ExcludedIndexes, N',', 1)) [x] ON [t].[index_name] LIKE [x].[result];
	END;
	
	IF NOT EXISTS (SELECT NULL FROM [#target_indexes]) BEGIN 
		PRINT N'No matches found for provided inputs.';
		RETURN 0;  
	END;
	
	CREATE TABLE #indexes_to_script (
		[schema_name] sysname NOT NULL,
		[object_type] char(1) NOT NULL,
		[object_id] int NOT NULL,
		[object_name] sysname NOT NULL,
		[index_id] int NOT NULL,
		[index_name] sysname NULL,
		[type_desc] nvarchar(60) NULL,
		[key_columns] nvarchar(4000) NULL,
		[included_columns] nvarchar(4000) NULL,
		[is_unique] bit NULL,
		[data_space_id] int NULL,
		[dataspace_name] sysname NULL,
		[partition_scheme_name] sysname NULL,
		[ignore_dup_key] bit NULL,
		[is_primary_key] bit NULL,
		[is_unique_constraint] bit NULL,
		[fill_factor] tinyint NOT NULL,
		[is_padded] bit NULL,
		[is_disabled] bit NULL,
		--[is_ignored_in_optimization] bit NULL,
		[allow_row_locks] bit NULL,
		[allow_page_locks] bit NULL,
		[filter_definition] nvarchar(max) NULL,
		[compression_delay] int NULL,
		--[suppress_dup_key_messages] bit NULL,  -- 2017+
		--[optimize_for_sequential_key] bit NULL  -- 2019+ 
	);

	-- TODO: figure out a) IF [is_ignored_in_optimization] is something that needs to be part of IX definitions/scripts and b) when it was added 
	IF (SELECT dbo.[get_engine_version]()) >= 14.00 BEGIN 
		ALTER TABLE [#indexes_to_script] ADD [suppress_dup_key_messages] bit NULL;
	END;

	IF (SELECT dbo.[get_engine_version]()) >= 15.00 BEGIN 
		ALTER TABLE [#indexes_to_script] ADD [optimize_for_sequential_key] bit NULL;
	END;

	SET @sql = N'	SELECT 
		[s].[name] [schema_name],
		[x].[object_type],
		[x].[object_id],
		[x].[object_name],
		[x].[index_id],
		[x].[index_name],
		[i].[type_desc],
		--(SELECT STRING_AGG((QUOTENAME([c].[name]) + CASE WHEN [ic].[is_descending_key] = 1 THEN N'' DESC'' ELSE N'''' END), N'', '') WITHIN GROUP(ORDER BY [ic].[key_ordinal]) FROM [{0}].sys.[index_columns] [ic] INNER JOIN [{0}].sys.columns [c] ON [ic].[object_id] = [c].[object_id] AND [ic].[column_id] = [c].[column_id] WHERE [ic].[key_ordinal] > 0 AND [ic].[object_id] = [i].[object_id] AND [ic].[index_id] = [i].[index_id]) [key_columns], 
		--(SELECT STRING_AGG(QUOTENAME([c].[name]), N'', '') WITHIN GROUP(ORDER BY [ic].[key_ordinal]) FROM [{0}].sys.[index_columns] [ic] INNER JOIN [{0}].sys.columns [c] ON [ic].[object_id] = [c].[object_id] AND [ic].[column_id] = [c].[column_id] WHERE [ic].[is_included_column] = 1 AND [ic].[object_id] = [i].[object_id] AND [ic].[index_id] = [i].[index_id]) [included_columns],
		(SELECT STUFF((SELECT N'', '' + QUOTENAME([c].[name]) + CASE WHEN [ic].[is_descending_key] = 1 THEN N'' DESC'' ELSE N'''' END FROM [{0}].sys.[index_columns] [ic] INNER JOIN [{0}].sys.columns [c] ON [ic].[object_id] = [c].[object_id] AND [ic].[column_id] = [c].[column_id] WHERE [ic].[key_ordinal] > 0 AND [ic].[object_id] = [i].[object_id] AND [ic].[index_id] = [i].[index_id] ORDER BY [ic].[key_ordinal] FOR XML PATH(N''''), TYPE).value(N''.'', N''nvarchar(MAX)''), 1, 1, N'''')) [key_columns],
		(SELECT STUFF((SELECT N'', '' + QUOTENAME([c].[name]) FROM [{0}].sys.[index_columns] [ic] INNER JOIN [{0}].sys.columns [c] ON [ic].[object_id] = [c].[object_id] AND [ic].[column_id] = [c].[column_id] WHERE [ic].[is_included_column] = 1 AND [ic].[object_id] = [i].[object_id] AND [ic].[index_id] = [i].[index_id] ORDER BY [ic].[key_ordinal] FOR XML PATH(N''''), TYPE).value(N''.'', N''nvarchar(MAX)''), 1, 1, N'''')) [included_columns],
		[i].[is_unique],
		[i].[data_space_id], 
		[d].[name] [dataspace_name],
		[ps].[name] [partition_scheme_name], -- TODO: going to have to look up the columns? for this? 
		[i].[ignore_dup_key],
		[i].[is_primary_key],
		[i].[is_unique_constraint],
		[i].[fill_factor],
		[i].[is_padded],
		[i].[is_disabled],
		--[i].[is_ignored_in_optimization], -- no idea when this was added ... 
		[i].[allow_row_locks],
		[i].[allow_page_locks],
		--[i].[has_filter],
		[i].[filter_definition],
		[i].[compression_delay]
		--[i].[suppress_dup_key_messages],
		--[i].[optimize_for_sequential_key] 
	FROM 
		[{0}].sys.indexes [i]
		INNER JOIN [#target_indexes] [x] ON [i].[object_id] = [x].[object_id] AND [i].[index_id] = [x].[index_id]
		INNER JOIN [{0}].sys.[schemas] [s] ON [x].[schema_id] = [s].[schema_id]
		LEFT OUTER JOIN [{0}].sys.[data_spaces] [d] ON [i].[data_space_id] = [d].[data_space_id]
		LEFT OUTER JOIN [{0}].sys.[partition_schemes] [ps] ON [i].[data_space_id] = [ps].[data_space_id]
	ORDER BY 
		[x].[object_name], [i].[index_id]; ';

	SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);

	INSERT INTO [#indexes_to_script] (
		[schema_name],
		[object_type],
		[object_id],
		[object_name],
		[index_id],
		[index_name],
		[type_desc],
		[key_columns],
		[included_columns],
		[is_unique],
		[data_space_id],
		[dataspace_name],
		[partition_scheme_name],
		[ignore_dup_key],
		[is_primary_key],
		[is_unique_constraint],
		[fill_factor],
		[is_padded],
		[is_disabled],
		--[is_ignored_in_optimization],
		[allow_row_locks],
		[allow_page_locks],
		[filter_definition],
		[compression_delay]--,
		--[suppress_dup_key_messages],
		--[optimize_for_sequential_key]
	)
	EXEC sys.[sp_executesql]
		@sql;
	
	IF @ExcludeHeaps = 1 BEGIN 
		DELETE FROM [#indexes_to_script] WHERE [index_id] = 0;
	END;
	   
	WITH options AS ( 
		SELECT 
			[object_id], 
			[index_id],
			CASE WHEN [is_padded] = 1 THEN N'PAD_INDEX = ON, ' ELSE N'' END 
				-- + stats recompute (where do i even find this?)
				-- + compression (page/row/none (default))
				--		though... this can also be defined by partitions ... so... that's complex. 
				-- + sort_in_tempdb... (directive)
				-- + drop_existing... (directive)
				-- + ONLINE (directive)
			+ CASE WHEN [allow_row_locks] = 0 THEN N' ALLOW_ROW_LOCKS = OFF,' ELSE N'' END 
			+ CASE WHEN [allow_page_locks] = 0 THEN N' ALLOW_PAGE_LOCKS = OFF' ELSE N'' END
			+ CASE WHEN [fill_factor] <> 0 THEN N' FILLFACTOR = ' + CAST([fill_factor] AS sysname) + N', ' ELSE N'' END
			 [options]
		FROM 
			[#indexes_to_script] 
	)

	SELECT 
		IDENTITY(int, 1, 1) [row_id],
		[i].[object_id],
		[i].[index_id],
		[i].[schema_name], 
		[i].[object_name], 
		[i].[index_name],
		[i].[key_columns], 
		[i].[included_columns],
		CASE 
			WHEN [i].[index_id] = 0 THEN N'-- HEAP: ' + QUOTENAME([i].[schema_name]) + N'.' + QUOTENAME([object_name]) + @crlf
			ELSE 
				CASE WHEN [i].[is_primary_key] = 1 THEN N'ALTER TABLE ' + QUOTENAME([i].[schema_name]) + N'.' + QUOTENAME([object_name]) + N' ADD CONSTRAINT ' + QUOTENAME([i].[index_name]) + @crlf + N'PRIMARY KEY ' + CASE WHEN [i].[index_id] = 1 THEN N'CLUSTERED' ELSE N'' END + N' '
				ELSE N'CREATE ' + CASE WHEN [i].[is_unique] = 1 THEN N'UNIQUE' ELSE N'' END + CASE WHEN [i].[index_id] = 1 THEN N'' ELSE N'NON' END + N'CLUSTERED INDEX' + ' ' + QUOTENAME([i].[index_name]) + N' ' + @crlf + N'ON ' + QUOTENAME([i].[schema_name]) + N'.' + QUOTENAME([i].[object_name])
			END
			+ N' (' + LTRIM([i].[key_columns]) + N')'
			+ CASE WHEN [i].[included_columns] IS NOT NULL THEN @crlf + N'INCLUDE (' + LTRIM([i].[included_columns]) + N')' ELSE N'' END
			+ CASE WHEN [i].[filter_definition] IS NOT NULL THEN @crlf + N'WHERE ' + [i].[filter_definition] + N' ' ELSE N'' END
			+ CASE WHEN NULLIF(ISNULL([o].[options], N''), N'') IS NULL THEN N'' ELSE @crlf + N'WITH (' + LTRIM(LEFT([o].[options], LEN([o].[options]) - 1)) + N')' END
			+ @crlf + CASE WHEN [i].[partition_scheme_name] IS NULL THEN N'ON ' + QUOTENAME([i].[dataspace_name]) + N'' ELSE N'<partition_scheme_column_here>' END
			+ N';' + @crlf 
			
		END + @crlf [definition]
	INTO 
		#projected_indexes
	FROM 
		[#indexes_to_script] [i]
		INNER JOIN [options] [o] ON [i].[object_id] = [o].[object_id] AND [i].[index_id] = [o].[index_id]
	ORDER BY 
		[i].[object_name], [i].[index_id];

	IF (SELECT dbo.is_xml_empty(@SerializedOutput)) = 1 BEGIN -- RETURN instead of project.. 
		SELECT @SerializedOutput = (
			SELECT 
				[object_id],
				[index_id],
				[schema_name],
				[object_name],
				[index_name],
				[key_columns], 
				[included_columns],
				[definition]
			FROM 
				[#projected_indexes] 
			ORDER BY 
				[row_id]
			FOR XML PATH(N'object'), ROOT(N'objects'), TYPE
		);

		RETURN 0;
	END;

	-- otherwise: 
	DECLARE @output nvarchar(MAX) = N'';
	SELECT 
		@output = @output + [definition] 
	FROM 
		[#projected_indexes] 
	ORDER BY 
		[row_id];

	EXEC [dbo].[print_long_string] @output;

	RETURN 0; 
GO


-----------------------------------
USE [admindb];
GO

IF OBJECT_ID('dbo.list_index_metrics','P') IS NOT NULL
	DROP PROC dbo.[list_index_metrics];
GO

CREATE PROC dbo.[list_index_metrics]
	@TargetDatabase								sysname				= NULL,						-- can/will be derived by execution context. 
	@TargetTables								nvarchar(MAX)		= N'{ALL}',   
--	@ExcludedTables								nvarchar(MAX)		= NULL, 
	@ExcludeSystemTables						bit					= 1,
	@IncludeFragmentationMetrics				bit					= 0,						-- really don't care about this - 99% of the time... 
	@MinRequiredTableRowCount					int					= 0,						-- ignore tables with < rows than this value... (but, note: this is per TABLE, not per IX cuz filtered indexes might only have a few rows on a much larger table).
	@OrderBy									sysname				= N'ROW_COUNT',				-- { ROW_COUNT | FRAGMENTATION | SIZE | BUFFER_SIZE | READS | WRITES }
	@IncludeDefinition							bit					= 1,						 -- include/generate the exact definition needed for the IX... 
	@SerializedOutput							XML					= N'<default/>'	    OUTPUT
AS
    SET NOCOUNT ON; 

	-- [v12.8.5293.3] - License, Code, & Docs: https://github.com/overachiever-productions/s4/ 
		
	SET @TargetDatabase = NULLIF(@TargetDatabase, N'');

	IF @TargetDatabase IS NULL BEGIN 
		EXEC dbo.[get_executing_dbname] @ExecutingDBName = @TargetDatabase OUTPUT;
		
		IF @TargetDatabase IS NULL BEGIN 
			RAISERROR('Invalid Database-Name specified for @TargetDatabase and/or S4 was unable to determine calling-db-context. Please specify a valid database name for @TargetDatabase and retry. ', 16, 1);
			RETURN -5;
		END;
	END;

	SET @TargetTables = ISNULL(NULLIF(@TargetTables, N''), N'{ALL}'); 

	DECLARE @sql nvarchar(MAX);

-- TODO: @ExcludedTables ... and i think I only allow that to work with/against {ALL}
--		as in, if {ALL} then @Excluded can be set, otherwise... i guess there's a potential for @TargetTables to be a LIKE? 
	CREATE TABLE #target_tables (
		[table_name] sysname NOT NULL, 
		[object_id] int NULL
	);	
	
	IF @TargetTables <> N'{ALL}' BEGIN 
		INSERT INTO [#target_tables] ([table_name])
		SELECT [result] FROM dbo.split_string(@TargetTables, N',', 1);

		SET @sql = N'USE [' + @TargetDatabase + N'];
		UPDATE #target_tables 
		SET 
			[object_id] = OBJECT_ID([table_name])
		WHERE 
			[object_id] IS NULL; ';

		EXEC sp_executesql 
			@sql;
	
		IF EXISTS (SELECT NULL FROM [#target_tables] WHERE [object_id] IS NULL) BEGIN 
			SELECT [table_name] [target_table], [object_id] FROM [#target_tables] WHERE [object_id] IS NULL; 

			RAISERROR(N'One or more supplied @TargetTables could not be identified (i.e., does not have a valid object_id). Please remove or correct.', 16, 1);
			RETURN -11;
		END;
	END;

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Load Core Meta Data:
	-----------------------------------------------------------------------------------------------------------------------------------------------------
	SET @sql = N'	SELECT 
		[i].[object_id],
		[i].[index_id],
		[o].[type],
		[s].[name] [schema_name],
		[o].[name] [table_name],
		[i].[name] [index_name],
		[i].[type_desc],
		[i].[is_unique],
		[i].[ignore_dup_key],
		[i].[is_primary_key],
		[i].[is_unique_constraint],
		[i].[fill_factor],
		[i].[is_padded],
		[i].[is_disabled],
		[i].[is_hypothetical],
		[i].[allow_row_locks],
		[i].[allow_page_locks],
		[i].[filter_definition]
	FROM 
		[{0}].sys.indexes i
		INNER JOIN [{0}].sys.objects o ON [i].[object_id] = [o].[object_id]
		INNER JOIN [{0}].sys.[schemas] s ON [o].[schema_id] = [s].[schema_id]{where}; ';

	SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);

	IF @TargetTables = N'{ALL}' BEGIN 
		SET @sql = REPLACE(@sql, N'{where}', N'');
	  END;
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{where}', NCHAR(13) + NCHAR(10) + NCHAR(9) + N' WHERE ' + NCHAR(13) + NCHAR(10) + NCHAR(9) + NCHAR(9) + N'[i].[object_id] IN (SELECT [object_id] FROM [#target_tables])');
	END;

	CREATE TABLE #sys_indexes (
		[object_id] int NOT NULL,
		[index_id] int NOT NULL,
		[type] char(2) NULL,
		[schema_name] sysname NOT NULL,
		[table_name] sysname NOT NULL,
		[index_name] sysname NULL,
		[type_desc] nvarchar(60) NULL,
		[is_unique] bit NULL,
		[ignore_dup_key] bit NULL,
		[is_primary_key] bit NULL,
		[is_unique_constraint] bit NULL,
		[fill_factor] tinyint NOT NULL,
		[is_padded] bit NULL,
		[is_disabled] bit NULL,
		[is_hypothetical] bit NULL,
		[allow_row_locks] bit NULL,
		[allow_page_locks] bit NULL,
		[filter_definition] nvarchar(max) NULL
	);

	INSERT INTO [#sys_indexes] (
		[object_id],
		[index_id],
		[type],
		[schema_name],
		[table_name],
		[index_name],
		[type_desc],
		[is_unique],
		[ignore_dup_key],
		[is_primary_key],
		[is_unique_constraint],
		[fill_factor],
		[is_padded],
		[is_disabled],
		[is_hypothetical],
		[allow_row_locks],
		[allow_page_locks],
		[filter_definition]
	)
	EXEC [sys].[sp_executesql]
		@sql, 
		N'@TargetDatabase sysname', 
		@TargetDatabase = @TargetDatabase;

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Usage Stats:
	-----------------------------------------------------------------------------------------------------------------------------------------------------
	SET @sql = N'WITH usage_stats AS (
	SELECT
		obj.[object_id],
		ixs.[index_id],
		usage.[user_seeks], 
		usage.[user_scans], 
		usage.[user_lookups],
		usage.user_seeks + usage.user_scans + usage.user_lookups [reads],
		usage.[user_updates] [writes]
	FROM
		[{0}].sys.dm_db_index_usage_stats usage
		INNER JOIN [{0}].sys.indexes ixs ON usage.[object_id] = ixs.[object_id] AND ixs.[index_id] = usage.[index_id]
		INNER JOIN [{0}].sys.objects obj ON usage.[object_id] = obj.[object_id]
	WHERE
		usage.database_id = DB_ID(@TargetDatabase){targets}
	)

	SELECT
		[object_id],
		[index_id],
		[user_seeks], 
		[user_scans], 
		[user_lookups],
		[reads],
		[writes],
		CAST (
			CASE 
				WHEN writes = 0 THEN reads 
				WHEN reads = 0 AND writes > 0 THEN 0 - writes
				ELSE CAST(reads as decimal(24,2)) / CAST(writes as decimal(24,2))
			END 
		as decimal(24,2)) [read_write_ratio], 
		CAST( 
			CASE 
				WHEN user_seeks > user_scans THEN CAST(user_seeks AS decimal(24,2)) / (CAST(user_seeks AS decimal(24,2)) + CAST(user_scans AS decimal(24,2))) * 100.0 
				ELSE 0
			END
		AS decimal(5,2)) [seek_ratio]
	FROM
		usage_stats
	ORDER BY
		[read_write_ratio] DESC; ';

---------------------------------------------------------------------------------------------------------------------------------------
-- TODO: [ratio] calculations have 2x problems: a) short-circuiting of CASE operators (compare BOTH values); b) 'simple' ratio probably isn't the BEST approach here. 
-- vNEXT: address issue with simple-ratio not being the best option/outcome here... 
--			could be something as simple as: "likelihood of current use" ... HIGH,MED,LOW*,NONE* - where warnings would obviously go with low/none... 
--					and column name could be [usage] - and... could be based on the ratio > x ...and or situations where reads > xK or some PERCENTAGE of writes...
---------------------------------------------------------------------------------------------------------------------------------------

	SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);
	IF @TargetTables = N'{ALL}' BEGIN 
		SET @sql = REPLACE(@sql, N'{targets}', N'');
	  END;
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{targets}', NCHAR(13) + NCHAR(10) + NCHAR(9) + NCHAR(9) + N' AND obj.[object_id] IN (SELECT [object_id] FROM [#target_tables])');
	END;

	CREATE TABLE #usage_stats (
		[object_id] int NOT NULL,
		[index_id] int NOT NULL,
		[user_seeks] bigint NOT NULL,
		[user_scans] bigint NOT NULL,
		[user_lookups] bigint NOT NULL,
		[reads] bigint NULL,
		[writes] bigint NOT NULL,
		[read_write_ratio] decimal(24,2) NULL,
		[seek_ratio] decimal(5,2) NULL
	);

	INSERT INTO [#usage_stats] (
		[object_id],
		[index_id],
		[user_seeks],
		[user_scans],
		[user_lookups],
		[reads],
		[writes],
		[read_write_ratio],
		[seek_ratio]
	)
	EXEC [sys].[sp_executesql]
		@sql, 
		N'@TargetDatabase sysname', 
		@TargetDatabase = @TargetDatabase;

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Operational Stats
	-----------------------------------------------------------------------------------------------------------------------------------------------------
	SET @sql = N'WITH operational_stats AS ( 
		SELECT 
			object_id,
			index_id,
			SUM(row_lock_count) [row_lock_count],
			SUM(row_lock_wait_in_ms) [row_lock_waits],
			SUM(page_lock_count) [page_lock_count],
			SUM(page_lock_wait_in_ms) [page_lock_waits], 
			SUM(page_io_latch_wait_count) [page_io_latch_count],
			SUM(page_io_latch_wait_in_ms) [page_io_latch_waits]--, 
			-- TODO: output any of the following columns that make sense (into the operational_details xml):
			--SUM(leaf_insert_count) [leaf_insert_count],
			--SUM(leaf_delete_count) [leaf_delete_count],
			--SUM(leaf_update_count) [leaf_update_count],
			--SUM(leaf_ghost_count) [leaf_ghost_count],  -- does''t include retained rows via snapshot isolation
			--SUM(range_scan_count) [ranges_scan_count],
			--SUM(singleton_lookup_count) [seek_count],
			--SUM(forwarded_fetch_count) [forwarded_count]
		FROM 
			sys.dm_db_index_operational_stats(DB_ID(@TargetDatabase),NULL,NULL,NULL){where}
		GROUP BY 
			[object_id], index_id
	 )  

	 SELECT 
		[object_id], 
		index_id,
		[row_lock_waits] [row_lock_wait_MS],
		CASE 
			WHEN [row_lock_count] = 0 THEN 0
			ELSE CAST(CAST([row_lock_waits] AS decimal(24,2)) / CAST([row_lock_count] AS decimal(24,2)) AS decimal(24,2))
		END [avg_row_lock_wait], 
		[page_lock_waits] [page_lock_wait_MS],
		CASE 
			WHEN [page_lock_count] = 0 THEN 0
			ELSE CAST(CAST([page_lock_waits] AS decimal(24,2)) / CAST([page_lock_count] AS decimal(24,2)) AS decimal(24,2)) 
		END [avg_page_lock_wait], 
		[page_io_latch_waits] [page_io_latch_wait_MS],
		CASE
			WHEN [page_io_latch_count] = 0 THEN 0 
			ELSE CAST(CAST([page_io_latch_waits] AS decimal(24,2)) / CAST([page_io_latch_count] AS decimal(24,2)) AS decimal(24,2)) 
		END [avg_page_io_latch_wait] 
	FROM 
		operational_stats; ';

	SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);
	IF @TargetTables = N'{ALL}' BEGIN 
		SET @sql = REPLACE(@sql, N'{where}', N'');
	  END;
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{where}', NCHAR(13) + NCHAR(10) + NCHAR(9) + NCHAR(9) + N'WHERE' + NCHAR(13) + NCHAR(10) + NCHAR(9) + NCHAR(9) + NCHAR(9) + N' [object_id] IN (SELECT [object_id] FROM [#target_tables])');
	END;

	CREATE TABLE #operational_stats (
		[object_id] int NOT NULL,
		[index_id] int NOT NULL,
		[row_lock_wait_MS] bigint NULL,
		[avg_row_lock_wait] decimal(24,2) NULL,
		[page_lock_wait_MS] bigint NULL,
		[avg_page_lock_wait] decimal(24,2) NULL,
		[page_io_latch_wait_MS] bigint NULL,
		[avg_page_io_latch_wait] decimal(24,2) NULL
	);

	INSERT INTO [#operational_stats] (
		[object_id],
		[index_id],
		[row_lock_wait_MS],
		[avg_row_lock_wait],
		[page_lock_wait_MS],
		[avg_page_lock_wait],
		[page_io_latch_wait_MS],
		[avg_page_io_latch_wait]
	)
	EXEC [sys].[sp_executesql]
		@sql, 
		N'@TargetDatabase sysname', 
		@TargetDatabase = @TargetDatabase;

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Physical Stats:
	-----------------------------------------------------------------------------------------------------------------------------------------------------
	CREATE TABLE #physical_stats (
		[object_id] int NULL,
		[index_id] int NULL,
		[alloc_unit_type_desc] nvarchar(60) NULL,
		[index_depth] tinyint NULL,
		[index_level] tinyint NULL,
		[avg_fragmentation_percent] decimal(5,2) NULL,
		[fragment_count] bigint NULL
	);

	IF @IncludeFragmentationMetrics = 1 BEGIN
		DECLARE @targetObject int = 0; -- equivalent to DEFAULT

		SET @sql = N'SELECT
			[object_id],
			index_id,
			alloc_unit_type_desc, 
			index_depth, 
			index_level, 
			CAST(avg_fragmentation_in_percent AS decimal(5,2)) avg_fragmentation_percent,
			fragment_count
		FROM 
			sys.dm_db_index_physical_stats(DB_ID(@TargetDatabase), @targetObject, DEFAULT, DEFAULT, ''LIMITED''); ';

		IF @TargetTables <> N'{ALL}' BEGIN
				DECLARE [walker] CURSOR LOCAL FAST_FORWARD FOR 
				SELECT 
					[object_id]
				FROM 
					[#target_tables];
				
				OPEN [walker];
				FETCH NEXT FROM [walker] INTO @targetObject;
				
				WHILE @@FETCH_STATUS = 0 BEGIN
				
					INSERT INTO [#physical_stats] (
						[object_id],
						[index_id],
						[alloc_unit_type_desc],
						[index_depth],
						[index_level],
						[avg_fragmentation_percent],
						[fragment_count]
					)
					EXEC [sys].[sp_executesql]
						@sql, 
						N'@TargetDatabase sysname, @targetObject int', 
						@TargetDatabase = @TargetDatabase,
						@targetObject = @targetObject;					
				
					FETCH NEXT FROM [walker] INTO @targetObject;
				END;
				
				CLOSE [walker];
				DEALLOCATE [walker];		

		  END; 
		ELSE BEGIN
			INSERT INTO [#physical_stats] (
				[object_id],
				[index_id],
				[alloc_unit_type_desc],
				[index_depth],
				[index_level],
				[avg_fragmentation_percent],
				[fragment_count]
			)
			EXEC [sys].[sp_executesql]
				@sql, 
				N'@TargetDatabase sysname, @targetObject int', 
				@TargetDatabase = @TargetDatabase,
				@targetObject = @targetObject;
		END;
	END;

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Sizing Stats
	-----------------------------------------------------------------------------------------------------------------------------------------------------
	SET @sql = N'SELECT
			[t].[object_id],
			[i].[index_id],
			[p].[rows] AS [row_count],
			CAST(((SUM([a].[total_pages]) * 8) / 1024.0) AS decimal(24, 2)) AS [allocated_mb],
			CAST(((SUM([a].[used_pages]) * 8) / 1024.0) AS decimal(24, 2)) AS [used_mb],
			CAST((((SUM([a].[total_pages]) - SUM([a].[used_pages])) * 8) / 1024.0) AS decimal(24, 2)) AS [unused_mb]
		FROM
			[{0}].[sys].[tables] [t]
			INNER JOIN [{0}].[sys].[indexes] [i] ON [t].[object_id] = [i].[object_id]
			INNER JOIN [{0}].[sys].[partitions] [p] ON [i].[object_id] = [p].[object_id] AND [i].[index_id] = [p].[index_id]
			INNER JOIN [{0}].[sys].[allocation_units] [a] ON [p].[partition_id] = [a].[container_id]{where}
		GROUP BY
			[t].[object_id],
			[i].[index_id],
			[p].[rows]; ';

	SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);
	IF @TargetTables = N'{ALL}' BEGIN 
		SET @sql = REPLACE(@sql, N'{where}', N'');
	  END;
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{where}', NCHAR(13) + NCHAR(10) + NCHAR(9) + NCHAR(9) + N'WHERE' + NCHAR(13) + NCHAR(10) + NCHAR(9) + NCHAR(9) + NCHAR(9) + N' [t].[object_id] IN (SELECT [object_id] FROM [#target_tables])');
	END;

	CREATE TABLE #sizing_stats (
		[object_id] int NOT NULL,
		[index_id] int NOT NULL,
		[row_count] bigint NULL,
		[allocated_mb] decimal(24,2) NULL,
		[used_mb] decimal(24,2) NULL,
		[unused_mb] decimal(24,2) NULL
	);

	INSERT INTO [#sizing_stats] (
		[object_id],
		[index_id],
		[row_count],
		[allocated_mb],
		[used_mb],
		[unused_mb]
	)
	EXEC [sys].[sp_executesql]
		@sql, 
		N'@TargetDatabase sysname', 
		@TargetDatabase = @TargetDatabase;

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Buffering Stats
	-----------------------------------------------------------------------------------------------------------------------------------------------------
	SET @sql = N'SELECT
		allocation_unit_id,
		COUNT(*) cached_page_count
	INTO 
		#buffers	
	FROM 
		sys.dm_os_buffer_descriptors WITH(NOLOCK)
	WHERE 
		[database_id] = DB_ID(@TargetDatabase)
	GROUP BY 
		[allocation_unit_id];
	
	WITH buffered AS ( 
		SELECT
			p.[object_id],
			p.[index_id],
			CAST((buffers.[cached_page_count]) * 8.0 / 1024.0 AS decimal(24,2)) AS [buffered_size_mb]
		FROM
			[#buffers] buffers
			INNER JOIN [{0}].sys.allocation_units AS au ON au.[allocation_unit_id] = buffers.[allocation_unit_id]
			INNER JOIN [{0}].sys.partitions AS p ON au.[container_id] = p.[partition_id]
			INNER JOIN [{0}].sys.indexes AS i ON i.[index_id] = p.[index_id] AND p.[object_id] = i.[object_id]{objects}
	)

	SELECT
		b.[object_id], 
		b.index_id, 
		b.buffered_size_mb
	FROM 
		buffered b ; ';

-- TODO: may need to remap allocation_unit_ids to containers/targets as per example listed here: https://docs.microsoft.com/en-us/sql/relational-databases/system-dynamic-management-views/sys-dm-os-buffer-descriptors-transact-sql?view=sql-server-ver15#examples

	SET @sql = REPLACE(@sql, N'{0}', @TargetDatabase);
	IF @TargetTables = N'{ALL}' BEGIN 
		SET @sql = REPLACE(@sql, N'{objects}', N'');
	  END;
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{objects}', NCHAR(13) + NCHAR(10) + NCHAR(9) + NCHAR(9) + NCHAR(9) + N'INNER JOIN #target_tables [t] ON [p].[object_id] = [t].[object_id]');
	END;

	CREATE TABLE #buffer_stats (
		[object_id] int NOT NULL,
		[index_id] int NOT NULL,
		[buffered_size_mb] decimal(24,2) NULL
	);

	INSERT INTO [#buffer_stats] (
		[object_id],
		[index_id],
		[buffered_size_mb]
	)
	EXEC [sys].[sp_executesql]
		@sql, 
		N'@TargetDatabase sysname', 
		@TargetDatabase = @TargetDatabase;

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Index Definition:
	-----------------------------------------------------------------------------------------------------------------------------------------------------
	DECLARE @ixDefinitions xml; 
	EXEC dbo.[script_indexes]
		@TargetDatabase = @TargetDatabase,
		@ExcludeHeaps = 1,
		@IncludeSystemTables = 0,
		@IncludeViews = 0,
		@SerializedOutput = @ixDefinitions OUTPUT; 

	CREATE TABLE #definitions (
		[object_id] int NOT NULL, 
		[index_id] int NOT NULL,
		[key_columns] nvarchar(MAX) NOT NULL, 
		[included_columns] nvarchar(MAX) NULL, 
		[definition] nvarchar(MAX) NOT NULL,
		PRIMARY KEY CLUSTERED ([object_id], [index_id])
	);

	WITH shredded AS ( 
		SELECT 
			[data].[row].value(N'(object_id)[1]', N'int') [object_id],
			[data].[row].value(N'(index_id)[1]', N'int') [index_id],
			[data].[row].value(N'(key_columns)[1]', N'nvarchar(MAX)') [key_columns],
			[data].[row].value(N'(included_columns)[1]', N'nvarchar(MAX)') [included_columns],
			[data].[row].value(N'(definition)[1]', N'nvarchar(MAX)') [definition]
		FROM 
			@ixDefinitions.nodes(N'//object') [data]([row])
	)	

	INSERT INTO [#definitions] (
		[object_id],
		[index_id],
		[key_columns],
		[included_columns],
		[definition]
	)
	SELECT 
		[object_id],
		[index_id],
		[key_columns],
		[included_columns],
		[definition] 
	FROM 
		[shredded];

	-----------------------------------------------------------------------------------------------------------------------------------------------------
	-- Final Projection:
	----------------------------------------------------------------------------------------------------------------------------------------------------------
	SET @sql = N'WITH collapsed_physical_stats AS (
		SELECT 
			[object_id], 
			index_id,
			SUM([avg_fragmentation_percent]) [avg_fragmentation_percent], 
			SUM([fragment_count]) [fragment_count]
		FROM 
			[#physical_stats]	
		GROUP BY	
			[object_id], 
			index_id
	), 
	collapsed_buffer_stats AS ( 
		SELECT 
			[object_id], 
			index_id,
			SUM([buffered_size_mb]) [buffered_size_mb]
		FROM 
			[#buffer_stats]
		GROUP BY 
			[object_id], 
			index_id
	)

	{projectOrReturn}SELECT 
		--i.[object_id],
		i.[table_name],
		i.[index_id],
		ISNULL(i.[index_name], N'''') [index_name],
		--cd.[definition],
		[d].[key_columns], 
		[d].[included_columns],
		ss.[row_count],
		ISNULL(us.reads, 0) reads, 
		ISNULL(us.writes, 0) writes, 
		us.[read_write_ratio] [ratio], 
		{fragmentation_details}
		ss.allocated_mb, 
		ss.used_mb, 
		--ss.unused_mb,
		ISNULL(bs.buffered_size_mb, 0) cached_mb, 
		--br.buffered_percentage,
		ISNULL(us.user_seeks, 0) seeks, 
		ISNULL(us.user_scans, 0) scans, 
		ISNULL(us.user_lookups, 0) lookups,
		us.seek_ratio, 
		(SELECT 
			[name] ''@name'', [metric] ''@value''  FROM ( 
				VALUES
					(ISNULL(os.avg_row_lock_wait, 0), N''avg_row_lock_ms''),
					(ISNULL(os.avg_page_lock_wait, 0), N''avg_page_lock_ms''), 
					(ISNULL(os.avg_page_io_latch_wait, 0), N''avg_page_io_latch_ms'')
			) AS x([metric], [name])
		FOR XML PATH(''metric''), ROOT(''operational_metrics''), type) [operational_metrics]{definitions}
	FROM 
		#sys_indexes i
		--{IncludedTables}
		--{ExcludedTables}
		LEFT OUTER JOIN #usage_stats us ON i.[object_id] = us.[object_id] AND i.[index_id] = us.index_id
		LEFT OUTER JOIN #operational_stats os ON i.[object_id] = os.[object_id] AND i.index_id = os.index_id
		{physical_stats}
		LEFT OUTER JOIN #sizing_stats ss ON i.[object_id] = ss.[object_id] AND i.index_id = ss.index_id
		LEFT OUTER JOIN [collapsed_buffer_stats] bs ON i.[object_id] = bs.[object_id] AND i.index_id = bs.index_id
		--LEFT OUTER JOIN #column_definitions cd ON i.[object_id] = cd.[object_id] AND i.index_id = cd.index_id
		LEFT OUTER JOIN #definitions [d] ON [i].[object_id] = [d].[object_id] AND [i].[index_id] = [d].[index_id]
	WHERE
		1 = 1
		{IncludeSystemTables}
		{MinRequiredTableRowCount}
	ORDER BY 
		{ORDERBY} DESC{forxml}; ';

	-- predicates:
	IF @ExcludeSystemTables = 1 
		SET @sql = REPLACE(@sql, N'{IncludeSystemTables}', N'AND i.[type] = ''U'' '); -- exclude 'S'
	ELSE 
		SET @sql = REPLACE(@sql, N'{IncludeSystemTables}', N'');

	IF @MinRequiredTableRowCount <> 0 
		SET @sql = REPLACE(@sql, N'{MinRequiredTableRowCount}', N'AND ss.[row_count] > ' + CAST(@MinRequiredTableRowCount AS sysname));
	ELSE 
		SET @sql = REPLACE(@sql, N'{MinRequiredTableRowCount}', N'');

	IF @IncludeFragmentationMetrics = 1 BEGIN
		SET @sql = REPLACE(@sql, N'{fragmentation_details}', N'ps.avg_fragmentation_percent [fragmentation_%], ISNULL(ps.fragment_count, 0) [fragments],');
		SET @sql = REPLACE(@sql, N'{physical_stats}', N'LEFT OUTER JOIN [collapsed_physical_stats] ps ON i.[object_id] = ps.[object_id] AND i.index_id = ps.index_id');
	  END;
	ELSE BEGIN
		SET @sql = REPLACE(@sql, N'{fragmentation_details}', N'');
		SET @sql = REPLACE(@sql, N'{physical_stats}', N'');
	END;

	IF @TargetTables <> N'{ALL}' BEGIN 
		SET @sql = REPLACE(@sql, N'{IncludedTables}', N'INNER JOIN #target_tables targets ON i.table_name LIKE targets.table_name ');
	  END;
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{IncludedTables}', N'');
	END;

	-- etc... 
	DECLARE @crlf nchar(2) = NCHAR(13) + NCHAR(10);
	DECLARE @tab nchar(1) = NCHAR(9);
	IF @IncludeDefinition = 1 BEGIN 
		SET @sql = REPLACE(@sql, N'{definitions}', N',' + @crlf + @tab + N'[d].[definition] [index_definition]');
	  END;
	ELSE BEGIN 
		SET @sql = REPLACE(@sql, N'{definitions}', N'');
	END;

	--IF @ExcludedTables IS NOT NULL BEGIN 
		
	--	SELECT [result] [table_name] 
	--	INTO #excluded_tables
	--	FROM dbo.[split_string](@ExcludedTables, N',', 1);		

	--	SET @sql = REPLACE(@sql, N'{ExcludedTables}', N'INNER JOIN #excluded_tables excluded ON i.table_name NOT LIKE excluded.table_name');
	--  END; 
	--ELSE BEGIN 
	--	SET @sql = REPLACE(@sql, N'{ExcludedTables}', N'');
	--END;

	-- sort order:
	DECLARE @sort sysname; 
	SELECT @sort = CASE NULLIF(@OrderBy, N'')
		WHEN NULL THEN N'ss.[row_count]'
		WHEN N'FRAGMENTATION' THEN N'ps.avg_fragmentation_percent'
		WHEN N'SIZE' THEN N'ss.allocated_mb'
		WHEN N'BUFFER_SIZE' THEN N'bs.buffered_size_mb'
		WHEN N'READS' THEN N'us.reads'
		WHEN N'WRITES' THEN N'us.writes' 
		ELSE N'ss.[row_count]'
	END;

	SET @sql = REPLACE(@sql, N'{ORDERBY}', @sort);

	IF (SELECT dbo.is_xml_empty(@SerializedOutput)) = 1 BEGIN -- RETURN instead of project.. 
		DECLARE @crlftab nchar(3) = NCHAR(13) + NCHAR(10) + NCHAR(9);

		-- NOTE: the following line INCLUDES the ) for nesting the select @output = (nested_goes_here):
		SET @sql = REPLACE(@sql, N'{forxml}', @crlftab + N'FOR XML PATH(N''index''), ROOT (N''indexes''))');
		SET @sql = REPLACE(@sql, N'{projectOrReturn}', N'SELECT @output = (');

		--PRINT @sql;
		DECLARE @output xml;
		EXEC [sys].[sp_executesql]
			@sql, 
			N'@output xml OUTPUT', 
			@output = @output OUTPUT;

		SET @SerializedOutput = @output;

		RETURN 0;
	END;

	SET @sql = REPLACE(@sql, N'{forxml}', N'');
	SET @sql = REPLACE(@sql, N'{projectOrReturn}', N'');
	EXEC sp_executesql 
		@sql;

	RETURN 0;
GO


