    BEGIN; 
    	
    	DECLARE @output sysname;
		DECLARE @delimiter sysname = N'\'; -- hard-coded for now, but needs to be diff with linux... 

		WITH parts AS ( 
			SELECT 
				row_id, 
				[result]
			FROM 
