			    DROP CONSTRAINT DF_restore_log_test_date;       -- old/former name... 
        END

        IF OBJECT_ID(N'DF_restore_log_operation_type') IS NOT NULL BEGIN
		    ALTER TABLE dbo.restore_log
			    DROP CONSTRAINT DF_restore_log_operation_type;
        END

