--		DEALLOCATE [cycler];

--	END;

	-- Cycle Error Logs: 
	SET @currentStepName = N'Cycle Logs';
	SET @currentCommand = N'-- Error Log:
USE master;
GO
EXEC master.sys.sp_cycle_errorlog;
GO

-- SQL Server Agent Error Log:
USE msdb;
GO
EXEC dbo.sp_cycle_agent_errorlog;
