	EXEC msdb..sp_add_jobstep 
			@job_id = @jobId,               
			@step_id = @currentStepId,		
			@step_name = @currentStepName,	
			@subsystem = N'TSQL',			
			@command = @currentCommand,		
			@on_success_action = 1,	-- quit reporting success	
			@on_fail_action = 2,	-- quit reporting failure 
			@database_name = N'msdb',
			@retry_attempts = 2,
			@retry_interval = 1;

	RETURN 0;
