		@NormalizedName = @normalizedName OUTPUT, 
		@ObjectID = @sourceObjectID OUTPUT;

	IF @outcome <> 0
		RETURN @outcome;  -- error will have already been raised... 	

	CREATE TABLE #work (
		[row_id] int NOT NULL,
		[deadlock_id] sysname NULL,
		[timestamp] sysname NULL,
		[process_count] sysname NULL,
